import React, {useState} from 'react';

function Article(props) {
    const [title, setTitle] = useState('');

    const handlerChangeTitle = (event) => {
        setTitle(event.target.value);
    }

    return (
        <div className="card full-width">
            <h1
                onClick={props.changeColor} style={{ color: props.articleTitleColor }}>{props.articleTitle}</h1>
            <p>{props.children}</p>
            <p><b>You can change the article title here:</b></p>
            <input onChange={handlerChangeTitle} type="text"></input>
        </div>
    );
}

export default Article;
